package lec04;
import java.util.*;

public class For_Loop_Demo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		System.out.println("Enter Any Number:- ");
		int n = sc.nextInt();
		for(int i=0; i<=n; i++) {
			System.out.print(i+" ");
		}
	}
}
